import {
  Box,
  Text,
  Input,
  Button,
  FormControl,
  FormLabel,
  Center,
  VStack,
  Toast,
  useToast,
} from "@chakra-ui/react";
import axios from "axios";
import { useEffect, useState } from "react";
import { useFormik } from "formik";
import * as Yup from "yup";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import Navbar from "../navbar";

const LoginSchema = Yup.object().shape({
  email: Yup.string().email("Invalid Email").required("Email is required"),
  password: Yup.string()
    .min(8, "Password must be 8 characters minimum")
    .required("Password is required"),
});

function Login() {
  const toast = useToast();
  const [accounts, setAccounts] = useState([]);
  const navigate = useNavigate();
  let indexUser;
  const fetchDataLogin = async () => {
    try {
      const response = await axios.get("http://localhost:3000/users");
      setAccounts(response.data);
      console.log(response.data);
      console.log(`--Fetch Login Success--`);
    } catch (err) {
      console.log(err);
    }
  };

  const allEmail = accounts.map((item) => item.email);

  useEffect(() => {
    fetchDataLogin();
  }, []);

  const check = (email, password) => {
    if (allEmail.includes(email)) {
      const newEmail = accounts[allEmail.indexOf(email)];
      if (newEmail.password.includes(password)) {
        indexUser = allEmail.indexOf(email);
        console.log(indexUser[indexUser]);
        localStorage.setItem("akun", allEmail[indexUser]);
      }
      console.log(localStorage);
      if (newEmail.password.includes(password)) {
        navigate("/saptweet");
      } else {
        toast({
          title: "Password Salah",
          status: "error",
          position: "top",
          isClosable: "true",
        });
      }
    } else {
      toast({
        title: "Email Belum Terdaftar",
        status: "error",
        position: "top",
        isClosable: "true",
      });
    }
  };

  const formik = useFormik({
    initialValues: {
      email: "",
      password: "",
    },
    validationSchema: LoginSchema,
    onSubmit: (values) => {
      check(values.email, values.password);
    },
  });

  return (
    <Box>
      <Navbar />
      <Box p={"0 5em"} pt={"5em"}>
        <Box>
          <form onSubmit={formik.handleSubmit}>
            <Box>
              <Box>
                <Center fontSize={"2xl"} as="b">
                  LOGIN
                </Center>
              </Box>
              <FormControl>
                <FormLabel>Email</FormLabel>
                <Input
                  type="text"
                  name="email"
                  size={"lg"}
                  value={formik.values.email}
                  onChange={formik.handleChange}
                  bgColor="transparent"
                  border="2px solid gray"
                  color="black"
                />
              </FormControl>
              <FormControl
                display="flex"
                flexDirection="column"
                justifyContent="center"
              >
                <FormLabel>Password</FormLabel>
                <Input
                  type="password"
                  name="password"
                  size={"lg"}
                  value={formik.values.password}
                  onChange={formik.handleChange}
                  bgColor="transparent"
                  border="2px solid gray"
                  color="black"
                />
              </FormControl>
              <Box display={"flex"} justifyContent={"center"}>
                <Button
                  as={"b"}
                  bgColor={"#3876BF"}
                  textColor={"white"}
                  mt={".5em"}
                  type={"submit"}
                  p=".5em 1em"
                  _hover={"none"}
                >
                  Login
                </Button>
              </Box>
            </Box>
          </form>
          <Box>
            <VStack>
              <Text>
                Belum punya akun?{" "}
                <Text as="b">
                  <Link to="/register">Register</Link>
                </Text>
              </Text>
            </VStack>
          </Box>
        </Box>
      </Box>
    </Box>
  );
}

export default Login;
